import { React, useState, useMemo, useCallback, useRef, useEffect } from 'react'
import { Sidebar } from './sidebar';
import { Grid } from './grid';
import './listing.Module.css';
import { UseDataFetch } from '../../../../organisms/helpers';
import { getItemsForCategory } from './helpers';

export default function Listing() {
    const [category, setCategory] = useState("");
    const data = UseDataFetch("items");
    const previousCallback = useRef(null);
    //const categorizedItems = getItemsForCategory(category, data);
    const categorizedItems = useMemo(() => {
        console.log(getItemsForCategory(category, data));
        return getItemsForCategory(category, data)
    }, []);

    const setCategoryId = useCallback(id => setCategory(id), []);


    useEffect(() => {
        if (previousCallback.current === categorizedItems)
            console.log('Same');
        else
            console.log('Not Same');

        console.log(previousCallback.current);
        console.log(categorizedItems);

        previousCallback.current = categorizedItems;
    }, [category]);

    return (
        <main className="flex list">
            <Sidebar setCategoryId={setCategoryId} />
            <Grid categorizedItems={categorizedItems} />
        </main>
    )
}
