import { useState, useEffect } from "react";
import _ from 'lodash';

export default function UseDataFetch(listCategory) {
  const [data, setData] = useState([]);

  const fetchItems = () => {
    fetch(`http://localhost:3002/${listCategory}`)
      .then(data => data.json())
      .then((data) => { setData(data) });
  };

  useEffect(() => {
    fetchItems();
  }, []);

  return data;
}
