import React from 'react'
import './sidebar.Module.css';
import { UseDataFetch } from '../../helper.organisms';
import { renderCategory } from './helper.sidebar';
export default function Sidebar({ setCategoryId }) {
   //use loader in UseDataFetch
   const Categories = UseDataFetch("categories");

   return (
      <ul className="side-bar">
         {
            Categories.map(
               renderCategory,{
                  category, setCategoryId
               })
         }
      </ul>
   )
}
