import { ReaderItem } from "../../../organisms/constants/ItemReader";

function getItems(items, count) {
    const show=[]
    items.for((item)=>{
        let key = ReaderItem.getProductName(item);
        if (count.get(ReaderItem.getId(item)) > 0) {
            show.push(
                { [key]: count.get(ReaderItem.getId(item)) }
            );
        }
    })
    return show;
}
export {getItems}
