import React from 'react'
import '../App.css';
import {
    items
 } from "./data_stub.js";


export default function Grid() {
const rows = [];
for(let i=0;i<items.length;i+=4)
{
    const row = items.slice(i,i+4);
    rows.push(row);
}
  return (
      <div className="menu">
            <div className = "product-desc flex">
               <p className="title">Buy Fresh Vegetables Online</p>
               <form className="sort flex align-center">
                  <p><b>Sort By</b></p>
                  <select name="sort-by-filter" id="sort-by-filter">
                     <option value="Relevance">Relevance</option>
                     <option value="Price">Price</option>
                     <option value="Popularity">Popularity</option>
                  </select>
               </form>
            </div>
            <div className="card">
                { 
                rows.map((row,index) => (
               <div key = {index} className="row flex">
                {
                    row.map(item=>(
                  <section key = {item.id} className="product">
                     <div className="discount">
                        <p className = "flex justify-center">10% OFF</p>
                     </div>
                     <div className="product-image-container justify-center"><img src={item.image_url} alt ={item.product_name} className="align-center"/></div>
                     <div className="source-btn">
                        <p className="source justify-center">Sourced at 5AM</p>
                     </div>
                     <div className="product-title">
                        <p>{item.product_name}</p>
                     </div>
                     <div className="weight-style flex">
                        <p className="weight">1kg</p>
                     </div>
                     <div className="cart-grid flex">
                        <p className="price flex"><span className="final-price">₹{item.price}</span>
                           <span className="cut-price">₹{item.discount_price}</span>
                        </p>
                        <div className="cart-btn-container align-center flex">
                           <button>ADD</button>
                        </div>
                     </div>
                  </section>
                  ))
}
                  </div>
                ))
}
                  </div>
                  </div>
  )
}
