import { useState, useEffect } from "react";
import _ from 'lodash';

function extractCategory(data) {
  return {
    "display_text": item.display_text,
    "image_url": item.image_url,
    "id": item.id,
    "active": item.active,
    "product_count": item.product_count,
    "action": {
      "url": item.action.url,
      "type": item.action.type
    }
  };
}

function extractItem(data) {
  return {
    "product_name": data.product_name,
    "image_url": data.image_url,
    "id": data.id,
    "price": data.price,
    "discount_price": data.discount_price,
    "currency": data.currency,
    "categories": [data.categories]
  };
}

export default function UseDataFetch(listCategory) {
  const [items, setItems] = useState([]);

  const fetchItems = () => {
    fetch(`http://localhost:3002/${listCategory}`)
      .then(data => data.json())
      .then(data => {
        let newData = [];

        console.log(data, listCategory);

        if (listCategory === "items")
          newItems = data.items.map(extractItem);
        else
          newItems = data.categories.map(extractCategory);

        setItems(newItems);
      });
  };

  useEffect(() => {
    fetchItems();
  }, []);

  return items;
}
