import React from 'react'
import './sidebar.Module.css';
import { UseDataFetch } from '../../../../../organisms/helpers';
import { renderCategory } from './helpers';
export default function Sidebar({ setCategoryId }) {
   //use loader in UseDataFetch
   const Categories = UseDataFetch("categories");
   function DisplaySideBar(Categories)
   {
      return Categories.map(
         renderCategory, {
         setCategoryId
      })
   }
   return (
      <ul className="side-bar">
         {
            DisplaySideBar(Categories)
         }
      </ul>
   )
}
