import {React, useState} from 'react'
import Sidebar from './Sidebar/Sidebar.js';
import {Grid} from './Card';
import './Listing.Module.css';
import useData from './UseDataFetch.js';

function getItemsByCategory(category, items) {
    if(category==='all') {
        return items;
    }
    else {
        return items?.filter(({categories})=>categories.includes(category));
    }
}

export default function Listing() {
    const [category,setCategory] = useState('all');
    const data = useData("items");
    const products = getItemsByCategory(category, data);
    const onProduct = (id) => {
        setCategory(id);
    };
  return (
    <main className = "flex main">
        <Sidebar onProduct = {onProduct} />
        <Grid products = {products}/>
        </main>
  )
}
