import React from 'react'
import './Sidebar.Module.css';
import Image from '../../../atoms/image/Image.js';
import useData from '../HelperUseDataFetch.js';
import { Paragraph } from '../../../atoms';

export default function Sidebar({setCategoryId}) {
   //use loader in useData
const cat = useData("categories"); //refactor
  return (
     <ul className="side-bar">
        {
        cat.map(category =>(
            <li key = {category.id} className="side-bar-list flex" onClick={()=>setCategory(category.id)}> 
            {/* inline function and usecall back and reader */}
               <Image source={category.image_url} alternate = "All" styles = "food-image"/>
               <Paragraph children={category.display_text}/> 
               {/* dont use paragraph component */}
               {/* use correct jsx and */}
            </li>
            ))
        }
        </ul>
  )
}
