import React from "react";
import "./Card.Module.css";
import Card from "./Card.Module.js";
import {Sortby} from "../../../molecules";
import { Paragraph } from "../../../atoms";

export default function Grid(props) {
  const rows = [];
  for (let i = 0; i < props.products.length; i += 4) {
    const row = props.products.slice(i, i + 4);
    rows.push(row);
  }
  return (
    <div className="menu">
      <div className="product-desc flex">
        <Paragraph styles="title" children="Buy Fresh Vegetables Online" />
        <Sortby
          styles="sort flex align-center"
          selectStyles="sort-by-filter"
          sort={["Relevance", "Popularity", "Price"]}
        />
      </div>
      <div className="card">
        {rows.map((row, index) => (
          <div key={index} className="row flex">
            {row.map((item) => (
              <Card key={item.id} name={item} />
            ))}
          </div>
        ))}
      </div>
    </div>
  );
}
