import React from 'react'
import { Button } from '../../../../../../../atoms';
import { useSelector, useDispatch } from 'react-redux';
import { incNumber,decNumber } from '../../../../../../../redux/actions/action';

export default function DisplayAddButton(props) {
    const count = useSelector((state) => state.changeNumber);
    const dispatch = useDispatch();

    function handleOnClick(changeCountFnc, id){
    if (changeCountFnc === "add")
      dispatch(incNumber(id));
    else if (changeCountFnc === "sub")
      dispatch(decNumber(id));
  }
  return (
      <div className="cart-btn-container align-center flex">
          {count.get(props.id) ? (
              <div className="cart-btn-container align-center flex cart-btn">
                  <Button
                      styles="subtract-btn"
                      onclick={()=>handleOnClick("sub",props.id)}
                      children="-"
                  />
                  <p className="count-show">
                      {count.get(props.id)}
                  </p>
                  <Button
                      styles="add-btn"
                      onclick={() => handleOnClick("add", props.id)}
                      children="+"
                  />
              </div>
          ) : (
              <Button
                  styles="cart-btn-container align-center flex cart-btn"
                      onclick={() => handleOnClick("sub", props.id)}
                  children="ADD"
              />
          )}
      </div>

  )
}
