import React from 'react'
import './sidebar.Module.css';
import useData from '../HelperUseDataFetch.js';
import { renderCategory } from './helper';
export default function Sidebar({setCategoryId}) {
   //use loader in useData
const Categories = useData("categories");

  return (
     <ul className="side-bar">
        {
         Categories.map((category)=>renderCategory(category,setCategoryId))
        }
        </ul>
  )
}
