import { React, useState } from 'react'
import Sidebar from './Sidebar/Sidebar.js';
import { Grid } from './card';
import './Listing.Module.css';
import useData from './HelperUseDataFetch.js';
//enum use

function filterByCategory(item, category) {
    return item.categories.includes(category);
}

function getItemsForCategory(category, items) {
    const filteredItems = items.filter(function (item) {
        return filterByCategory(item, category);
    });

    if (filteredItems.length==0) {
        return items;
    }
    else {
        return filteredItems; //use reader
    }
}

export default function Listing() {
    const [category, setCategory] = useState(); 
    const data = useData("items"); 
    //use memoization
    const products = getItemsForCategory(category, data); //dont use wrong variable names

    //rename getItemsForCategory
    const onProduct = (id) => { //redundant function, no need of onProduct function
        setCategory(id);
    };
    return (
        //classname main is wrong
        <main className="flex main"> 
            <Sidebar onProduct={onProduct} />
            <Grid products={products} />
        </main>
    )
}
