export default function useData(type) {
const baseURL = `http://localhost:3000/${type}`;
const [cat, setCat] = useState([]);

const fetCat = () => { fetch('http://localhost:3000/items')
.then(res => res.json())
.then(({categories}) => {setCat([...categories])})
}
useEffect(() => { fetCat() }, []);
    return cat;
}
