import React from "react";
import "./Card.Module.css";
import { Button } from "../../../atoms";
import { useSelector, useDispatch } from "react-redux";
import { incNumber, decNumber } from "../../../actions/index.js";
import { CardDiscountTag } from "./molecules/cardDiscountTag";
import { CardProductImg } from "./molecules/cardProductImg";
import { CardProductPrice } from "./molecules/cardProductPrice";
import { CardProductTitle } from "./molecules/cardProductTitle";
import { CardProductWeight } from "./cardProductWeight";
import { CardSourcedAt } from "./cardSourcedAt";

export default function Card(props) {
  const count = useSelector((state) => state.changeNumber);
  const dispatch = useDispatch();

  const handleOnClick=(changeCountFnc, id) =>{
    if (changeCountFnc === "add")
      dispatch(incNumber(id));
    else if (changeCountFnc === "sub")
      dispatch(decNumber(id));
  }
  return (
    <section className="product">
      <CardDiscountTag children="10% OFF" />
      <CardProductImg source={props.name.image_url} alternate={props.name.product_name} />
      <CardSourcedAt children="Sourced at 5AM" />
      <CardProductTitle children={props.name.product_name} />
      <CardProductWeight children="1kg" />

      <div className="cart-grid flex">
        <CardProductPrice finalPrice={props.name.discount_price}
          cutPrice={props.name.price} />

        <div className="cart-btn-container align-center flex">

          {count.get(props.name.id) ? (
            <div className="cart-btn-container align-center flex cart-btn">
              <Button
                styles="subtract-btn"
                onclick={() => dispatch(decNumber(props.name.id))}
                children="-"
              />
              <p className="count-show">
                {count.get(props.name.id)}
              </p>
              <Button
                styles="add-btn"
                onclick={() => dispatch(incNumber(props.name.id))}
                children="+"
              />
            </div>
          ) : (
            <Button
              styles="cart-btn-container align-center flex cart-btn"
              onclick={() => dispatch(incNumber(props.name.id))}
              children="ADD"
            />
          )}
        </div>
      </div>
    </section>
  );
}
