import { React, useCallback } from "react"

function RenderUseCallBack(setCategoryId,id)
{
    //console.log(id)

        useCallback(()=>{
            return setCategoryId(id)
        },[id])
}

function renderCategory(category)
{
    return (<li key={category.id} className="side-bar-list flex" onClick={() => useCallback(() => {
         this.setCategoryId(category.id)
    }, [category.id]) }>
        {/* usecall back and reader */}
       <img src={category.image_url} alt = "All" className = "food-image"/>
        <p>{category.display_text}</p>
    </li>)
}
export {renderCategory}
