import { useState, useEffect } from "react";

export default function useData(listCategory) {
  const baseURL = `http://localhost:3002/${listCategory}`; // dont use unnecessary variable
  const [items, setItems] = useState([]);


  const fetchItems = () => {
    fetch(baseURL) //write it in helper function
      .then((res) => res.json())
      .then((res) => {
          setItems([...res[listCategory]]); //use destructuring
      }); //dont write inline function
  }; //dont use res, instead use items by listCategory

  useEffect(() => {
    fetchItems();
  },[]);
  return items;
}
