import { useState, useEffect } from "react";
export default function UseDataFetch(listCategory) {
  const [items, setItems] = useState([]);
  const fetchItems = () => {
    fetch(`http://localhost:3002/${listCategory}`)
      .then((items) => items.json())
      .then((items) => setItems([...items[listCategory]])
      );
  };
  useEffect(() => {
    fetchItems();
  }, []);

  function mapItem(item,listCategory) {
    if(listCategory==="item")
    return (
      {
        "product_name": item.product_name,
        "image_url": item.image_url,
        "id": item.id,
        "price": item.price,
        "discount_price": item.discount_price,
        "currency": item.currency,
        "categories": [item.categories]
      }
    )
    else
    return(
      {
        "product_name": item.product_name,
        "image_url": item.image_url,
        "id": item.id,
        "price": item.price,
        "discount_price": item.discount_price,
        "currency": item.currency,
        "categories": [item.categories]
      }
    )
  }

  const ArrayItems = []
  ArrayItems.push(items.map((item) => { return (mapItem(item,listCategory)) }))

  console.log(ArrayItems);

  return items;
}
