function filterByCategory(item) {
    return item.categories.includes(this.category);
}
function getItemsForCategory(category, items) {
    // const filteredItems = items.filter(
    //     filterByCategory, {
    //     category
    // })
    const filteredItemsPromise = new Promise((resolve, reject) => {
    const filteredItems = items.filter(filterByCategory, { category });
        if (filteredItems.length > 0) {
            resolve(filteredItems);
        } else {
            reject("No items found");
        }
    });
    filteredItemsPromise.then((filteredItems) => {
        console.log(filteredItems)
    }).catch((error) => {
        // handle the error message
    });
     //use reader
}
export { getItemsForCategory }
