import { React, useState } from 'react'
import Sidebar from './Sidebar/Sidebar.js';
import { Grid } from './card';
import './Listing.Module.css';
import useData from './HelperUseDataFetch.js';
import { getItemsForCategory } from './helper.listing';
//enum use

export default function Listing() {
    const [category, setCategory] = useState(); 
    const data = useData("items"); 
    //use memoization
    const categorizedItems = getItemsForCategory(category, data);
    const setCategoryId = (id) => { 
        setCategory(id);
    };
    return (
        <main className="flex list"> 
            <Sidebar setCategoryId={setCategoryId} />
            <Grid categorizedItems={categorizedItems} />
        </main>
    )
}
