import React from 'react'
import './bill.css'
import { useSelector } from 'react-redux';
import { UseDataFetch } from '../helper.organisms';

export default function Bill() {
    const count = useSelector((state) => state.changeNumber);
    const items = HelperUseDataFetch("items")
    let show=[]

    for(let i=0;i<items.length;i++)
    {
        let key=items[i].product_name;
        if(count.get(items[i].id)>0)
        {   
            show.push(
                { [key]: count.get(items[i].id)}
            );
        }
    } 

  return (
    <div className="bill">
        <h1>Bill</h1>
    <ul className="cart-items">
            {
            show.map(
                (element)=>{
                    let key = Object.keys(element)
                    return <li key={key}>{key} : {element[key]}</li>
                }
            )}
    </ul>
    </div>
  )
}
