import items from '../Components/assets/DataStub.js'

const initalState=0;
const changeNumber = (state=initalState, action)=> {
    console.log(items);

    switch(action.type){
        case "INCREMENT" : return (items.find((items)=>items.categories===action.id)?state + 1:state);

        case "DECREMENT" : return (items.find((items)=>items.categories===action.id)?state - 1:state);
        default          : return state;
    }
}
export default changeNumber;
