import {React, useState} from 'react'
//import {items} from './data_stub.js';
import Sidebar from './Sidebar.js';
import Grid from './Grid.js';


function fetchCategories(category,items)
{
    if(category==='all')
    {
        return items;
    }
    else
    {
        return items?.filter(({categories})=>categories.includes(category));
    }
}
export default function Listing() {
    const [items, setItems] = useState([]);

    const fetchItems = () => { fetch('http://localhost:3001/items')
         .then(res => res.json())
         .then(({items}) => setItems(...items) )
        }

    const [category,setCategory] = useState('all');
    const products = fetchCategories(category,items);

    const onProduct = (id) => {
        setCategory(id);
    };

  return (
    <main className = "flex">
        <Sidebar onProduct = {onProduct} />
        <Grid products = {products}/>
        </main>
  )
}
