import fData from '../Components/Listing/data.js';
import React from 'react';
import items from './'
const initalState=0;
function changeNumber(state=initalState, action){
    //const {useData} = useData;
    console.log(fData().items);
    //const product = fData();
    //console.log("------",product)

    switch(action.type){
        case "INCREMENT" : return state + 1;
        case "DECREMENT" : return state - 1;
        default          : return state;
    }
}
export default changeNumber;
