import items from '../Components/assets/DataStub.js';

const initalState=new Map();

function changeInc(state,id)
{
    state[id]+=1;
    const newarr=[...state];
    return newarr;
}
function changeDec(state,id)
{
    if(state[id]<=0)
    return state;

    state[id]-=1;
    const newarr=[...state];
    return newarr;
}
const changeNumber = (state=initalState, action) =>{
    switch(action.type){

        case "INCREMENT" : return (state.has(action.Id)?(state.set(action.Id,state.get(action.Id)+1)):state);
        case "DECREMENT" : return (items.find((item)=>item.id===action.Id)?(changeDec(state,action.Id-1)):state);
        default          : return state;  
    }
}
export default changeNumber;
