import items from '../Components/assets/DataStub.js';

const initalState=[];
for(let i=0;i<items.length;i++)
{
    initalState[i]=0;
}
console.log(initalState);
function change(state,id)
{
    state[id-1]=state[id-1]+1;
    return state;
}
const changeNumber = (state=initalState, action) =>{
    console.log(state);

    switch(action.type){
        case "INCREMENT" : return (items.find((item)=>item.id==action.Id)?(change(state,action.Id-1)):state);
        case "DECREMENT" : return (items.find((item)=>item.id==action.Id)?(state[action.Id]-1):state[action.Id]);
        default          : return state;
    }
}
export default changeNumber;
