import React from 'react'
import '../App.css';
import {
    items
 } from "./data_stub.js";
import Card from "./Card.js";

export default function Grid() {
const rows = [];
const cat=[];
for(let i=0;i<items.length;i++)
{
   if("957"==items.categories)
   cat.push(items[i]);
}
for(let i=0;i<cat.length;i+=4)
{
    const row = cat.slice(i,i+4);
    rows.push(row);
}
  return (
      <div className="menu">
            <div className = "product-desc flex">
               <p className="title">Buy Fresh Vegetables Online</p>
               <form className="sort flex align-center">
                  <p><b>Sort By</b></p>
                  <select name="sort-by-filter" id="sort-by-filter">
                     <option value="Relevance">Relevance</option>
                     <option value="Price">Price</option>
                     <option value="Popularity">Popularity</option>
                  </select>
               </form>
            </div>
            <div className="card">
                { 
                rows.map((row,index) => (
               <div key = {index} className="row flex">
                {
                    row.map(item=>(
                     <Card key = {item.id} name={item}/>
                  ))
}
                  </div>
                ))
}
                  </div>
                  </div>
  )
}
