import { Image } from "../../../../atoms"

function setCategoryFunc(category,setCategoryId){
    setCategoryId(category.id)
}
function renderCategory(category, setCategoryId)
{
    return (<li key={category.id} className="side-bar-list flex" onClick={setCategoryFunc(category,setCategoryId)}>
        {/* inline function and usecall back and reader */}
        <Image source={category.image_url} alternate="All" styles="food-image" />
        <p>{category.display_text}</p>
    </li>)
}
export {renderCategory}
