import { useState, useEffect } from "react";
import _ from 'lodash';

export default function UseDataFetch(listCategory) {
  const [data, setData] = useState([]);

  const fetchItems = () => {
    fetch(`http://localhost:3002/${listCategory}`)
      .then(data => data.json())
      .then(data => {
        let list = [];
        let extractorFn;

        if (listCategory === "items") {
          list = data.items;
          extractorFn = extractItem;
        }
        else {
          list = data.categories;
          extractorFn = extractCategory;
        }

        const newData = list.map(extractorFn);

        setData(newData);
      });
  };

  useEffect(() => {
    fetchItems();
  }, []);

  return data;
}
