import {useState, useEffect} from 'react'

export default function useData(type) {

    const [items, setItems] = useState([]);
    const fetchItems = () => { fetch(`http://localhost:3002/${type}`)
    .then(res => res.json())
    .then(({items}) => {setItems([...res[type]])})
   }
   useEffect(() => {fetchItems() }, []);
   console.log(items);
  return {items};
}
