import React from "react";
import "./grid.css";
import Card from "../card/Card.js";
import { SortBy } from "./molecules";
import { cardNumber } from "./helper/Helper.cardNumber";

export default function Grid(props) {

  const rows = cardNumber(props.categorizedItems,[])


  function displaySingleRow(row,index)
  {
    return(
       <div key={index} className="row flex">
        {row.map((item) => (
          <Card key={item.id} name={item} /> //dont use inline function
        ))}
      </div>
    )

  }
  return (
    <div className="menu">
      <SortBy children="Buy Fresh Vegetables Online" sort={["Relevance","Popularity","Price"]}/>
      <div className="card">
        {
        rows.map((row,index) => (displaySingleRow(row,index)))
        }
      </div>
    </div>
  );
}
