import React from 'react'
import './bill.css'
import { useSelector } from 'react-redux';
import { UseDataFetch } from '../../../../organisms/helpers';
import { ReaderItem } from '../../../../organisms/constants/ItemReader';
export default function Bill() {

    const count = useSelector((state) => state.changeNumber);
    const items = UseDataFetch("items")
    let show = []

    function getItems(items,count,show)
    {
        for (let i = 0; i < items.length; i++) {
            let key = ReaderItem.getProductName(items[i]);
            if (count.get(items[i].id) > 0) {
                show.push(
                    { [key]: count.get(ReaderItem.getId(items[i])) }
                );
            }
        }
        return show
    }
   const FinalCartItems = getItems(items,count,show);

    return (
        <div className="bill">
            <h1>Bill</h1>
            <ul className="cart-items">
                {
                    show.map(
                        (element) => {
                            let key = Object.keys(element)
                            return <li key={key}>{key} : {element[key]}</li>
                        }
                    )}
            </ul>
        </div>
    )
}
