import React from 'react'
import './bill.css'
import { useSelector } from 'react-redux';
import { UseDataFetch } from '../../../../organisms/helpers';
import { getItems } from '../../helpers/Bill.Helper.GetItems';
import { showCartItems } from '../../helpers/Bill.Helper.ShowCartItems';
import { totalPrice } from '../../helpers/Bill.Helper.TotalPrice';
export default function Bill() {

    const count = useSelector((state) => state.changeNumber);
    const items = UseDataFetch("items")
    return (
        <div className="bill">
            <h1>Bill</h1>
            <table className="cart-items">
                <tbody>
                <tr>Items</tr>
                <tr></tr>
                <tr>Qty</tr>
                </tbody>
                <tbody>{showCartItems(getItems(items, count))}</tbody>
            </table>
            <div className="total-price">
                <h3>
                    Grand Total: â‚¹{totalPrice(items, count)}
                </h3>
            </div>
        </div>
    )
}
