import React from "react";
import "./Card.Module.css";
import { Button, Image, Paragraph } from "../../../atoms";
import { useSelector, useDispatch } from "react-redux";
import { incNumber, decNumber } from "../../../actions/index.js";
import { CardDiscountTag, Price } from "../../../molecules";

export default function Card(props) {
  const count = useSelector((state) => state.changeNumber);
  const dispatch = useDispatch();
  return (
    <section className="product">
      <CardDiscountTag children="10% OFF"/>
      <div className="product-image-container justify-center">
        <Image
          source={props.name.image_url}
          alternate={props.name.product_name}
          styles="align-center"
        />
      </div>
      <CardItemDescDiv
        styles="source-btn"
        ParaStyles="source justify-center"
        children="Sourced at 5AM"
      />
      <CardItemDescDiv
        styles="product-title"
        ParaStyles=""
        children={props.name.product_name}
      />
      <CardItemDescDiv
        styles="weight-style flex"
        ParaStyles="weight"
        children="1kg"
      />
      <div className="cart-grid flex">
        <Price
          styles="price flex"
          finalPriceStyles="final-price"
          cutPriceStyles="cut-price"
          finalPrice={props.name.discount_price}
          cutPrice={props.name.price}
        />
        <div className="cart-btn-container align-center flex">
          <>
            {count.get(props.name.id) ? (
              <div className="cart-btn-container align-center flex cart-btn">
                <Button
                  styles="subtract-btn"
                  onclick={() => dispatch(decNumber(props.name.id))}
                  children="-"
                />
                <Paragraph
                  styles="count-show"
                  children={count.get(props.name.id)}
                />
                <Button
                  styles="add-btn"
                  onclick={() => dispatch(incNumber(props.name.id))}
                  children="+"
                />
              </div>
            ) : (
              <Button
                styles="cart-btn-container align-center flex cart-btn"
                onclick={() => dispatch(incNumber(props.name.id))}
                children="ADD"
              />
            )}
          </>
        </div>
      </div>
    </section>
  );
}
