import { React, useState } from 'react'
import Sidebar from './Sidebar/Sidebar.js';
import { Grid } from './card';
import './Listing.Module.css';
import useData from './HelperUseDataFetch.js';
//enum use
function getItemsByCategory(category, items) {
    if (category === 'all') { //no need of all category
        return items;
    }
    else {
        return items?.filter(({ categories }) => categories.includes(category)); //use reader
    }//dont write inline function
}

export default function Listing() {
    const [category, setCategory] = useState('all'); //remove all from here
    const data = useData("items"); //
    const products = getItemsByCategory(category, data); //dont use wrong variable names
    //rename getItemsForCategory
    const onProduct = (id) => { //redundant function, no need of onProduct function
        setCategory(id);
    };
    return (
        <main className="flex main">
            <Sidebar onProduct={onProduct} />
            <Grid products={products} />
        </main>
    )
}
