import { useState, useEffect } from "react";

export default function HelpeUseDataFetch(listCategory) {
  const [items, setItems] = useState([]);
  const fetchItems = () => {
    fetch(`http://localhost:3002/${listCategory}`) //write it in helper function
      .then((items) => items.json())
      .then((items) => {
          setItems([...items[listCategory]]); //use destructuring
      }); //dont write inline function
  }; //dont use items, instead use items by listCategory

  useEffect(() => {
    fetchItems();
  },[]);
  return items;
}
