import items from '../Components/assets/DataStub.js';

const initalState=0;
const changeNumber = (state=initalState, action) =>{
    //console.log(action.Id);
    items.find((item)=>item.id==action.Id)?console.log("item.id"):console.log("j");
    switch(action.type){
        case "INCREMENT" : return (items.find((item)=>item.id==action.Id)?(state+1):state);
        case "DECREMENT" : return state - 1;
        default          : return state;
    }
}
export default changeNumber;
