import { useState, useEffect } from "react";
import _ from 'lodash'
export default function UseDataFetch(listCategory) {
  const [items, setItems] = useState([]);
  const fetchItems = () => {
    fetch(`http://localhost:3002/${listCategory}`)
      .then((items) => items.json())
      .then((items) => setItems([...items[listCategory]])
      );
  };
  useEffect(() => {
    fetchItems();
  }, []);
  function ReaderFunc(item,listCategory) {
    if(listCategory==="items")
    return (
      {
        "product_name": item.product_name,
        "image_url": item.image_url,
        "id": item.id,
        "price": item.price,
        "discount_price": item.discount_price,
        "currency": item.currency,
        "categories": [item.categories]
      }
    )
    else if(listCategory==="categories")
    return(
      {
        "display_text": item.display_text,
        "image_url": item.image_url,
        "id": item.id,
        "active": item.active,
        "product_count": item.product_count,
        "action": {
          "url": item.action.url,
          "type": item.action.type
        }
      }
    )
  }

  return new Promise((resolve, reject) => {
    try {
      const ReaderItems = [];
      items.forEach((item) => {
        ReaderFunc(item, listCategory)
          .then((result) => {
            ReaderItems.push(result);
            console.log(result);
            if (ReaderItems.length === items.length) {
              resolve(ReaderItems);
            }
          })
          .catch((error) => {
            reject(error);
          });
      });
    } catch (error) {
      reject(error);
    }
  });
}
